import React from 'react';
import { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
  const [enteredFilterYear, setEnteredFilterYear] = useState('2022');

  const filterChangeHandler = (event) => {
    setEnteredFilterYear(event.target.value);
    const filterYear = event.target.value
    console.log(filterYear);
    props.onSaveFilterYear(filterYear);
    };


  return (

    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select value={enteredFilterYear} onChange={filterChangeHandler}>
          <option  value='2022'>2022</option>
          <option value='2021'>2021</option>
          <option value='2020'>2020</option>
          <option value='2019'>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
